data.command:
const wardFlagName = "Arcane Ward Details";
async function updateActorResource(actor, wardStrength, wardStrengthMax) {
	let {wardStrengthField, wardStrengthMaxField} = getWardFieldNames(actor);
	if (!wardStrengthField || !wardStrengthMaxField) return;
	actorUpdate = {};
	actorUpdate[wardStrengthField] = wardStrength;
	actorUpdate[wardStrengthMaxField] = wardStrengthMax;
	return actor.update(actorUpdate)
}
async function setWardFieldNames(item) {
	const consumeTargetField = `data.${item.data.data.consume.target ?? ""}`;
	const maxField = consumeTargetField.replace(".value", ".max");
	return item.parent.setFlag("midi-qol", wardFlagName, {
		wardStrengthField: consumeTargetField,
		wardStrengthMaxField: maxField
	})
}
function getWardFieldNames(actor) {
	return actor.getFlag("midi-qol", wardFlagName) ?? {wardStrengthField: undefined, wardStrengthMaxField: undefined};
}
function getWardValue(actor) {
	const {wardStrengthField, wardStrengthMaxField} = getWardFieldNames(actor);
	if (!wardStrengthField || !wardStrengthMaxField) 
		return {wardStrengthField: undefined, wardStrengthMaxField: undefined};
	return {
		wardStrength: getProperty(item.parent.data, wardStrengthField),
		wardStrengthMax: getProperty(item.parent.data, wardStrengthMaxField)
	}
}

if (args[0] === "on") {
	const lastArg = args[args.length - 1];
	const item = await fromUuid(lastArg.origin);
	const actor = item.parent;
	const wardStrengthMax = args[1] * 2 + args[2];
	if (Number(args[1]) === 0 && Number(args[2]) === 0) return;
	const speaker = ChatMessage.getSpeaker({ actor });
	ChatMessage.create({ content: `${item.name} strength ${wardStrengthMax} created`, speaker });
	await setWardFieldNames(item);
	return updateActorResource(item.actor, wardStrengthMax, wardStrengthMax);
} else if (args[0] === "off") {
	const lastArg = args[args.length - 1];
	const item = await fromUuid(lastArg.origin);
	const actor = await fromUuid(lastArg.actorUuid);
	const speaker = ChatMessage.getSpeaker({ actor });
	ChatMessage.create({ content: `${item.name} dismissed`, speaker });
	await updateActorResource(item.actor, 0, 0);
	return item.actor.unsetFlag("midi-qol", wardFlagName)
} else if (
	args[0].macroPass === "postActiveEffects" &&
	args[0].itemData?.data.school === "abj" &&
	args[0].itemData?.data.level > 0) {
	let { wardStrength, wardStrengthMax } = getWardValue(actor);
	if (wardStrengthMax === undefined) return;
	let newWardStrength = Math.min(wardStrength + args[0].spellLevel * 2, wardStrengthMax);
	const speaker = ChatMessage.getSpeaker({ actor });
	ChatMessage.create({
		content: `${args[0].itemData.name} gains ${args[0].spellLevel * 2} points to ${newWardStrength}/${wardStrengthMax}`,
		speaker,
	});
	if (wardStrength !== newWardStrength) {
		await updateActorResource(actor, newWardStrength, wardStrengthMax);
	}
} else if (args[0] === "onUpdateActor") {
	const lastArg = args[args.length - 1];
	let newHP = getProperty(lastArg.updates, "data.attributes.hp.value");
	let oldHP = lastArg.targetActor.data.data.attributes.hp.value;
	if (newHP && oldHP && newHP < oldHP) {
		const damage = oldHP - newHP;
		const {wardStrengthField, wardStrengthMaxField} = getWardFieldNames(lastArg.sourceActor);
		if (!wardStrengthField) return;
		let { wardStrengthMax, wardStrength } = getWardValue(actor);
		if (wardStrengthMax === undefined) return;
		const absorbed = Math.min(damage, wardStrength ?? 0);
		if (absorbed) {
			newHP = newHP + absorbed;
			const speaker = ChatMessage.getSpeaker({ actor: lastArg.targetActor });
			ChatMessage.create({
				content: `${lastArg.originItem.name} absorbs ${absorbed} of ${damage} points of damage.<br> Hp -> ${newHP}<br>Wardstrength -> ${wardStrength - absorbed}`,
				speaker
			});
			lastArg.updates.data.attributes.hp.value = newHP;
			if (wardStrengthField !== "") { // assume the target is a resource
				setProperty(lastArg.updates, wardStrengthField, wardStrength - absorbed );
			}
		}
	}
	return true;
}

