const version = "0.9.53";
try {
    if (args[0].itemData.type !== "spell") return;
    // no healing done?
    if (!(args[0].damageList.some(li => li.oldHP < li.newHP))) return;
    // only targets self?
    if (!(args[0].hitTargetUuids.some(uuid => uuid !== args[0].tokenUuid))) return;
    // await (new Promise(resolve => setTimeout(resolve, 100)))


    const tactor = await fromUuid(args[0].actorUuid);
    const spellLevel = args[0].spellLevel;
    const currentHP = tactor.data.data.attributes.hp.value;
    const newHP = Math.min(tactor.data.data.attributes.hp.max, tactor.data.data.attributes.hp.value + 2 + spellLevel);
    ChatMessage.create({content: `${tactor.name} cures ${newHP - currentHP} HP of bonus healing`})
    return tactor.update({"data.attributes.hp.value": newHP});
} catch (err) {
    console.error(`${args[0].itemData.name} - Blessed Healer ${version}`, err);
}

