const version = "11.4.20";
try {
    if (rolledItem.type !== "spell" || rolledItem.system.actionType !== "heal") return;
    // no healing done?
    if (workflow.v3Damages) {
        if (!Object.entries(workflow.v3Damages).some(entry => {
            const [uuid, damages] = entry;
            return damages.appliedDamage < 0;
        })) return;
    } else if (!(workflow.damageList?.some(li => li.oldHP < li.newHP))) return;
    // only targets self?

    if (!(workflow.hitTargets.some(t => t.uuid !== workflow.tokenUuid))) return;
    // await (new Promise(resolve => setTimeout(resolve, 100)))


    const tactor = workflow.actor;
    const spellLevel = args[0].spellLevel;
    const currentHP = tactor.system.attributes.hp.value;
    const newHP = Math.min(tactor.system.attributes.hp.max, tactor.system.attributes.hp.value + 2 + spellLevel);
    ChatMessage.create({content: `${tactor.name} cures ${newHP - currentHP} HP of bonus healing`})
    return tactor.update({"system.attributes.hp.value": newHP});
} catch (err) {
    console.error(`${macroItem.name} - Blessed Healer ${version}`, err);
}
