data.command:
// Sample Slayers prey
if (args[0].tag === "OnUse") {
    let targetUuid = args[0].hitTargetUuids[0];
    if (!actor || !targetUuid) {
      console.error("Slayers's Prey: no token/target selected", targetUuid, actor);
      return;
    }

    // First remove the old effects from the target and ourselves 
    let item = actor.items.get(args[0].id);

    // have to remove the effect from old target if we are changing targets
    let oldTargetUuid = getProperty(actor.data, "flags.midi-qol.slayersPrey");
    if (oldTargetUuid && oldTargetUuid !== targetUuid) await DAE.deleteActiveEffect(oldTargetUuid, item.uuid)

    // Check if we already have the effect on ourselves.
    let effect = actor.effects.find(e=>e.data.origin === args[0].itemUuid);

    const effectData = {
      changes: [
        {key: "flags.midi-qol.slayersPrey", mode: 5, value: targetUuid, priority: 20}, // who is marked
        {key: "flags.dnd5e.DamageBonusMacro", mode: 5, value: `ItemMacro.${args[0].item.name}`, priority: 20}, // macro to apply the damage
      ],
      origin: args[0].uuid, //flag the effect as associated to the spell being cast
      disabled: false,
      duration: args[0].item.effects[0].duration,
      icon: args[0].item.img,
      label: args[0].item.name
    }

    if (effect) await effect.update(effectData);
    else await actor.createEmbeddedDocuments("ActiveEffect", [effectData]);
} else if (args[0].tag === "DamageBonus") {
    if (args[0].hitTargets.length === 0) return;
    const targetUuid = args[0].hitTargetUuids[0];
    // only weapon attacks
    if (!["mwak","rwak"].includes(args[0].item.data.actionType)) return {};
    // only on the marked target
    if (targetUuid !== getProperty(args[0].actor.flags, "midi-qol.slayersPrey")) return {};
    let damageType = args[0].item.data.damage.parts[0][1];
    return {damageRoll: `1d6[${damageType}]`, flavor: "Slayers Prey Damage"}
}

